/**
 * 公共命名空间
 */
declare namespace ComN {
    /**
     * 相应数据接口
     * server响应的数据必须满足这个接口
     */
    export interface IResData<C extends number = number, D = any> {
        /** 响应时间戳 */
        timestamp: number;
        /** 响应状态码 */
        statusCode: C;
        /** 响应附带消息 */
        mes: string;
        /** 响应数据 */
        data: D;
        /** 是否压缩 */
        ifCompress: boolean;
        /** 是否加密 */
        ifEncrypt: boolean;
        /** 处理后的数据，是一个json格式的字符串 */
        _data: string;
    }

    /**
     * 分页数据
     */
    export interface IPageData<T = any> {
        list: T[];
        length: number;
    }
    /**
     * 分页数据类型
     */
    export interface IPageQuery<Q = any> {
        /** 分页大小 */
        size: number;
        /** 第几页 */
        page: number;
        /** 查询条件 */
        query?: Q;
    }
}