/**
 * 公共命名空间
 */
declare namespace ComN {
    /** 数据处理类型 */
    export type dataHandlesType = 'z' | 'e' | 'o';

    /**
     * 数据处理
     */
    export interface IDataHandle {
        /** 数据处理列表，z表示压缩，e表示加密，o表示混淆 */
        'x-data-handles'?: dataHandlesType[];
    }

    /**
     * 请求头
     */
    export interface IReqHead extends IDataHandle {
        /** 用户的token */
        'x-token': string;
        /** 后端接口需要验证的暗号 */
        'x-secret-code': string;
    }

    /**
     * 相应数据接口
     * server响应的数据必须满足这个接口
     */
    export interface IResData<C extends number = number, D = any> extends IDataHandle {
        /** 响应时间戳 */
        timestamp: number;
        /** 响应状态码 */
        statusCode: C;
        /** 响应附带消息 */
        mes: string;
        /** 响应数据 */
        data: D | null;
    }

    /**
     * 分页数据
     */
    export interface IPageData<T = any> {
        list: T[];
        length: number;
    }
    /**
     * 分页数据类型
     */
    export interface IPageQuery<Q = any> {
        /** 分页大小 */
        size: number;
        /** 第几页 */
        page: number;
        /** 查询条件 */
        query?: Q;
    }
}